from __future__ import print_function

import os.path
import sys

from ..wheelfile import WheelFile


def unpack(path, dest='.'):
    """Unpack a wheel.

    Wheel content will be unpacked to ***REMOVED***dest***REMOVED***/***REMOVED***name***REMOVED***-***REMOVED***ver***REMOVED***, where ***REMOVED***name***REMOVED***
    is the package name and ***REMOVED***ver***REMOVED*** its version.

    :param path: The path to the wheel.
    :param dest: Destination directory (default to current directory).
    """
    with WheelFile(path) as wf:
        namever = wf.parsed_filename.group('namever')
        destination = os.path.join(dest, namever)
        print("Unpacking to: ***REMOVED******REMOVED***...".format(destination), end='')
        sys.stdout.flush()
        wf.extractall(destination)

    print('OK')
